CREATE TABLE force_amount (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, project_id BIGINT NOT NULL, transaction_id VARCHAR(255), first_name VARCHAR(255), last_name VARCHAR(255), email_address VARCHAR(255) NOT NULL, username VARCHAR(128) NOT NULL, country VARCHAR(50), state VARCHAR(50), city VARCHAR(50), address VARCHAR(255), postal_code VARCHAR(10), phone VARCHAR(25), mobile_phone VARCHAR(25), project_amount DECIMAL(10, 2) NOT NULL, site_amount DECIMAL(10, 2) NOT NULL, total_amount DECIMAL(10, 2) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), INDEX project_id_idx (project_id), PRIMARY KEY(id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_article (id BIGINT AUTO_INCREMENT, name VARCHAR(255), description TEXT, public TINYINT(1) DEFAULT '1', slug VARCHAR(255), UNIQUE INDEX force_article_sluggable_idx (slug), PRIMARY KEY(id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_content (id BIGINT AUTO_INCREMENT, name VARCHAR(255), description TEXT, slug VARCHAR(255), UNIQUE INDEX force_content_sluggable_idx (slug), PRIMARY KEY(id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_project (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, category_id BIGINT NOT NULL, status_id BIGINT NOT NULL, name VARCHAR(255) NOT NULL, logo VARCHAR(255), description TEXT, idea TEXT, market TEXT, team TEXT, target BIGINT, percent SMALLINT, started_at datetime NOT NULL, ended_at datetime NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, slug VARCHAR(255), UNIQUE INDEX force_project_sluggable_idx (slug), INDEX user_id_idx (user_id), INDEX status_id_idx (status_id), INDEX category_id_idx (category_id), PRIMARY KEY(id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_project_category (id BIGINT AUTO_INCREMENT, name VARCHAR(50) NOT NULL, public TINYINT(1) DEFAULT '1', slug VARCHAR(255), UNIQUE INDEX force_project_category_sluggable_idx (slug), PRIMARY KEY(id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_project_status (id BIGINT AUTO_INCREMENT, name VARCHAR(20) NOT NULL, slug VARCHAR(255), UNIQUE INDEX force_project_status_sluggable_idx (slug), PRIMARY KEY(id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_guard_forgot_password (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, unique_key VARCHAR(255), expires_at DATETIME NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_guard_group (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_guard_group_permission (group_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(group_id, permission_id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_guard_permission (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_guard_remember_key (id BIGINT AUTO_INCREMENT, user_id BIGINT, remember_key VARCHAR(32), ip_address VARCHAR(50), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_guard_user (id BIGINT AUTO_INCREMENT, first_name VARCHAR(255), last_name VARCHAR(255), email_address VARCHAR(255) NOT NULL UNIQUE, username VARCHAR(128) NOT NULL UNIQUE, algorithm VARCHAR(128) DEFAULT 'sha1' NOT NULL, salt VARCHAR(128), password VARCHAR(128), is_active TINYINT(1) DEFAULT '1', is_super_admin TINYINT(1) DEFAULT '0', last_login DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX is_active_idx_idx (is_active), PRIMARY KEY(id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_guard_user_group (user_id BIGINT, group_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, group_id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_guard_user_permission (user_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, permission_id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE force_guard_user_profile (user_id BIGINT, country VARCHAR(50), state VARCHAR(50), city VARCHAR(50), address VARCHAR(255), postal_code VARCHAR(10), organization VARCHAR(50), phone VARCHAR(25), mobile_phone VARCHAR(25), description TEXT, avatar LONGBLOB, image LONGBLOB, facebook VARCHAR(255), twitter VARCHAR(255), youtube VARCHAR(255), website VARCHAR(255), PRIMARY KEY(user_id)) COLLATE utf8_unicode_ci ENGINE = INNODB;
